package com.evolvedbinary.rocksdb.cb.github;

import java.net.URISyntaxException;
import java.net.URL;
import java.nio.file.Path;
import java.nio.file.Paths;

/**
 * The self-signed certificate for
 * testing is generated by the Maven
 * profile: self-signed-ssl-certificate
 */
public class TestSSLCertificate {

    public static Path getKeyStorePath() throws URISyntaxException {
        final URL testKeyStoreUrl = TestSSLCertificate.class.getResource("/ss-keystore.p12");
        return Paths.get(testKeyStoreUrl.toURI()).toAbsolutePath();
    }

    public static String getKeyStorePass() {
        return "secret";
    }

    public static String getCertPass() {
        return "secret";
    }

    public static Path getTrustStorePath() throws URISyntaxException {
        return getKeyStorePath();
    }

    public static String getTrustStorePass() {
        return "secret";
    }
}
